import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Container, Divider } from '@mui/material';
import { makeStyles } from '@mui/styles';
import { Link } from 'react-router-dom'; // 페이지 이동을 위한 Link 컴포넌트

const useStyles = makeStyles((theme) => ({
  logo: {
    display: 'flex',
    alignItems: 'center',
    color: theme.palette.primary.contrastText,
  },
  logoText: {
    marginLeft: theme.spacing(1),
    fontSize: '20px',
    fontWeight: 'bold',
  },
  buttonContainer: {
    marginLeft: 'auto',
    display: 'flex',
    gap: theme.spacing(2),
  },
  button: {
    color: theme.palette.primary.contrastText,
    '&:hover': {
      backgroundColor: theme.palette.primary.light,
    },
  },
  divider: {
    margin: theme.spacing(2, 0),
  },
}));

const NavigationBar = ({ onSafetyBoxClick, onCCTVClick, activePage }) => {
  const classes = useStyles();

  return (
    <div>
      <AppBar position="sticky" color="primary">
        <Toolbar>
          <Box className={classes.logo}>
            <img src="/logo.png" alt="Logo" width={40} height={40} />
            <Typography variant="h6" className={classes.logoText}>
              IoT Dashboard
            </Typography>
          </Box>

          <Box className={classes.buttonContainer}>
            <Button className={classes.button} onClick={onSafetyBoxClick} component={Link} to="/safety-box">
              Safety Box Information
            </Button>
            <Button className={classes.button} onClick={onCCTVClick} component={Link} to="/cctv">
              Checking CCTV
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <Container>
        <Box p={3}>
          <Typography variant="h6" color="textSecondary">
            Current Page: {activePage}
          </Typography>
          <Divider className={classes.divider} />
        </Box>
      </Container>
    </div>
  );
};

export default NavigationBar;
