import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Box, Paper, Checkbox, FormControlLabel } from '@mui/material';

function DataGraph() {
  const [isResponsive, setIsResponsive] = useState(false); // 반응형 차트 사용 여부를 관리하는 상태

  // 데이터 설정
  const timeLabels = ['10:00', '10:05', '10:10', '10:15', '10:20']; // 시간 데이터
  const shockData = [0.2, 0.5, 1.0, 0.3, 0.4]; // 충격 센서 데이터
  const temperatureData = [20, 21, 22, 21, 20]; // 온도 센서 데이터

  return (
    <Box sx={{ width: '100%' }}>
      {/* 반응형 차트 사용 여부를 토글하는 체크박스 */}
      <FormControlLabel
        checked={isResponsive}
        control={
          <Checkbox onChange={(event) => setIsResponsive(event.target.checked)} />
        }
        label="Use responsive container"
        labelPlacement="end"
      />

      <Paper sx={{ width: '100%', height: 400 }} elevation={3}>
        {/* 반응형 차트 컨테이너 */}
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={shockData}>
            {/* 격자선 추가 */}
            <CartesianGrid strokeDasharray="3 3" />
            {/* X축 데이터 */}
            <XAxis dataKey={timeLabels} />
            {/* Y축 데이터 */}
            <YAxis />
            {/* 툴팁 */}
            <Tooltip />
            {/* 범례 */}
            <Legend />
            {/* 충격 센서 데이터 라인 */}
            <Line type="monotone" dataKey="shockData" stroke="#ff6384" />
            {/* 온도 센서 데이터 라인 */}
            <Line type="monotone" dataKey="temperatureData" stroke="#36a2eb" />
          </LineChart>
        </ResponsiveContainer>
      </Paper>
    </Box>
  );
}

export default DataGraph;
