import React from 'react';

const CCTVPage = () => {
 const handleDoorLock = async () => {
   try {
     const commandData = {
       "m2m:cin": {
         "rn": "command",
         "con": JSON.stringify({"command": "unlocked"})
       }
     };

     const unlockResponse = await fetch('http://localhost:8081/cse-mn/doorAE/command', {
       method: 'POST',
       mode: 'no-cors', // CORS 제한 해제
       headers: {
         'Content-Type': 'application/json;ty=4',
         'X-M2M-Origin': 'CdoorAE',
         'X-M2M-RI': '12345',
         'X-M2M-RVI': '3',
         'Accept': 'application/json'
       },
       body: JSON.stringify(commandData),
     });

     // no-cors 모드에서는 응답 본문을 읽을 수 없음
     alert('Door unlock request sent');
   } catch (error) {
     console.error('Detailed Error:', error);
     alert('An error occurred: ' + error.message);
   }
 };

 return (
   <div className="cctv-container">
     <div className="cctv-stream">
       <h2>CCTV Live Stream</h2>
       <div className="video-placeholder">
         CCTV Video Stream (Implementation Pending)
       </div>
     </div>
     <div className="door-control">
       <button onClick={handleDoorLock}>Unlock Door</button>
     </div>
   </div>
 );
};

export default CCTVPage;
