import React, { useState } from 'react';
import { Box, Button, Typography, CircularProgress } from '@mui/material'; // Material-UI 컴포넌트 추가

const CCTVPage = () => {
  const [lockTimeout, setLockTimeout] = useState(null); // CCTV Live Stream
  const [responseMessage, setResponseMessage] = useState(""); // 응답 메시지 상태 저장
  const [loading, setLoading] = useState(false); // 로딩 상태 추가
  const [recordings, setRecordings] = useState([]); // 녹화본 목록 상태
  const [selectedRecording, setSelectedRecording] = useState(null); // 선택된 녹화본 상태

  // 가상 녹화본 데이터
  const virtualRecordings = Array.from({ length: 10 }, (_, index) => ({
    id: index + 1,
    fileName: `recording_${index + 1}.mp4`,
    date: new Date(),
    imageUrl: `/images/recording_${index + 1}.jpg`, // 가상의 이미지 파일 경로
  }));

  const handleRecordingSelect = (recording) => {
    setSelectedRecording(recording); // 선택된 녹화본 설정
  };

  const handleApproveRecording = () => {
    // 녹화본 승인 처리
    if (selectedRecording) {
      setResponseMessage(`Recording ${selectedRecording.id} has been approved.`);
    }
  };

  const sendCommand = async (command) => {
    try {
      setLoading(true); // 요청 시작 시 로딩 상태 활성화
      const timestamp = new Date().toISOString();

      const commandData = {
        "m2m:cin": {
          "rn": `command_${timestamp.replace(/[:.]/g, '_')}`,
          "con": {
            "command": command,
            "timestamp": timestamp,
          },
        },
      };

      // Flask 서버로 POST 요청
      const response = await fetch('http://localhost:5000/send-command', { // Flask 서버 주소
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(commandData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server error: ${errorText}`);
      }

      // 응답 JSON 처리
      const data = await response.json();
      setResponseMessage(data.message || "Command executed successfully");
      alert(`${command} command sent successfully: ${data.message}`);
    } catch (error) {
      console.error('Error sending command:', error);
      setResponseMessage(`Error: ${error.message}`);
      alert(`An error occurred: ${error.message}`);
    } finally {
      setLoading(false); // 요청 완료 후 로딩 상태 비활성화
    }
  };

  const handleDoorLock = () => {
    sendCommand('unlocked');

    // 기존 타임아웃 클리어
    if (lockTimeout) {
      clearTimeout(lockTimeout);
    }

    // 1분 후 도어 잠금 명령
    const newTimeout = setTimeout(() => {
      sendCommand('locked');
      setLockTimeout(null);
    }, 600); // 1분 = 60000ms

    setLockTimeout(newTimeout);
  };

  return (
    <div className="cctv-container" style={{ display: 'flex', justifyContent: 'space-between', height: '100vh' }}>
      {/* 왼쪽 섹션: CCTV Live Stream */}
      <Box sx={{ width: '50%', height: '50%', padding: 3, backgroundColor: '#f5f5f5', borderRadius: 2, boxShadow: 3 }}>
        <Typography variant="h5" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive", marginTop: '20px' }}>
          CCTV Live Stream
        </Typography>
        <div className="video-placeholder" style={{ height: 'calc(100% - 50px)', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          {/* 비디오 파일을 public/videos 폴더에서 참조 */}
          {selectedRecording ? (
            <video width="100%" height="100%" controls autoPlay loop>
              <source src={`/videos/${selectedRecording.fileName}`} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <Typography variant="body1" color="textSecondary">
              No recording selected
            </Typography>
          )}
        </div>

      </Box>

      {/* 오른쪽 섹션: 녹화본 선택 */}
      <Box
        sx={{
          width: '50%',
          height: '50%',
          padding: 3,
          backgroundColor: "#f5f5f5",
          borderRadius: 2,
          boxShadow: 3,
        }}
      >
        <Typography variant="h6" align="center" sx={{ fontFamily: "'Sour Gummy', cursive", marginBottom: 2 }}>
          Select a Recording
        </Typography>
        <div className="recordings-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between' }}>
          {virtualRecordings.length > 0 ? (
            virtualRecordings.map((recording) => (
              <div
                key={recording.id}
                onClick={() => handleRecordingSelect(recording)}
                style={{
                  width: '30%', // 한 줄에 3개씩 배치
                  marginBottom: '20px',
                  cursor: 'pointer',
                  textAlign: 'center',
                }}
              >
                <img
                  src={recording.imageUrl}
                  alt={`Recording ${recording.id}`}
                  style={{
                    width: '100%',
                    height: '150px',
                    objectFit: 'cover',
                    borderRadius: '8px',
                  }}
                />
                <Typography variant="body2" color="textSecondary" sx={{ marginTop: 1 }}>
                  {`Recording ${recording.id}`}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  {new Date(recording.date).toLocaleDateString()}
                </Typography>
              </div>
            ))
          ) : (
            <Typography variant="body1" color="textSecondary">
              No recordings available
            </Typography>
          )}
        </div>

        {/* 승인 버튼 */}
        {selectedRecording && (
          <div className="approve-button" style={{ marginTop: '20px', textAlign: 'center' }}>
            <Button variant="contained" color="primary" onClick={handleApproveRecording}>
              Approve Recording
            </Button>
          </div>
        )}

        {/* 승인된 메시지 표시 */}
        {responseMessage && (
          <Typography variant="body1" color="green" align="center" sx={{ marginTop: '10px' }}>
            {responseMessage}
          </Typography>
        )}
      </Box>

      <div className="door-control" style={{ marginTop: '20px' }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleDoorLock}
          size="large"
          fullWidth
          sx={{
            backgroundColor: '#d32f2f', // 빨간색 배경
            '&:hover': {
              backgroundColor: '#c62828', // 호버 시 빨간색
            },
            padding: '12px', // 버튼 안쪽 여백
            fontSize: '18px', // 글자 크기
            fontWeight: '600', // 글자 두께
            fontFamily: "'Sour Gummy', cursive" 
          }}
          disabled={loading} // 로딩 중일 때 버튼 비활성화
        >
          {loading ? <CircularProgress size={24} sx={{ color: 'white' }} /> : 'Unlock Door'}
        </Button>
        {responseMessage && (
          <Typography variant="body1" color="textSecondary" sx={{ marginTop: '20px' }} >
            {responseMessage}
          </Typography>
        )}
      </div>
    </div>


  );
};

export default CCTVPage;
