import React, { useEffect, useState } from "react";
import { Map, CustomOverlayMap } from "react-kakao-maps-sdk";
import DataGraph from "./DataGraph";
import { Box, Typography, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, useTheme, TextField, Grid2 } from "@mui/material";

function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  const [location, setLocation] = useState(null);
  const [isStolen, setIsStolen] = useState(false);
  const [isArrived, setIsArrived] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [chatMessage, setChatMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [isFetching, setIsFetching] = useState(true);

  const theme = useTheme();

  const destination = {
    lat: 37.55062087654126,
    lng: 127.07425390537487,
  };

  const userName = "PP-P";

  const haversineDistance = (coords1, coords2) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371;
    const dLat = toRad(coords2.lat - coords1.lat);
    const dLon = toRad(coords2.lng - coords1.lng);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(coords1.lat)) *
        Math.cos(toRad(coords2.lat)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const fetchLocationData = async () => {
    try {
      const response = await fetch("http://localhost:5000/get-location", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        const newLocation = {
          lat: parseFloat(data.latitude),
          lng: parseFloat(data.longitude),
        };
        setLocation(newLocation);
        setIsStolen(data.isStolen);

        const distance = haversineDistance(newLocation, destination);
        if (distance < 0.1 && !isArrived) {
          setIsArrived(true);
          setShowPopup(true);
        }
      } else {
        console.error("Failed to fetch location data");
      }
    } catch (error) {
      console.error("Error fetching location:", error);
    }
  };

  useEffect(() => {
    if (!isFetching) return;

    const intervalId = setInterval(fetchLocationData, 5000);
    return () => clearInterval(intervalId);
  }, [isFetching, isArrived]);

  const handlePopupClose = (approved) => {
    setShowPopup(false);
    if (approved) {
      setIsFetching(false);
    } else {
      setIsFetching(true);
    }
  };

  const handleFetchLocation = () => {
    fetchLocationData();
  };

  const handleSendMessage = () => {
    if (chatMessage.trim()) {
      setChatHistory([...chatHistory, { user: userName, message: chatMessage }]);
      setChatMessage("");
    }
  };

  if (!location) {
    return (
      <div className="dashboard-container" style={{ textAlign: "center" }}>
        <Typography variant="h4" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          Loading location... ðŸ˜…
        </Typography>
      </div>
    );
  }

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        gap: 4,
        fontFamily: "'Sour Gummy', cursive",
        padding: 2,
        flexWrap: "wrap",
      }}
    >
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography
          variant="h6"
          align="center"
          gutterBottom
          sx={{ fontFamily: "'Sour Gummy', cursive", display: "flex", justifyContent: "space-between", alignItems: "center" }}
        >
          <span>SafetyBox Location</span>
          <Button
            variant="contained"
            size="small"
            onClick={handleFetchLocation}
            sx={{ fontFamily: "'Sour Gummy', cursive" }}
          >
            Refresh Location
          </Button>
        </Typography>
        <Map
          center={{ lat: location.lat, lng: location.lng }}
          level={3}
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          <CustomOverlayMap position={{ lat: location.lat, lng: location.lng }} zIndex={1}>
            <div style={{ position: "relative", display: "flex", flexDirection: "column", alignItems: "center" }}>
              <img src="/marker.png" alt="marker" style={{ width: "36px", height: "36px" }} />
              <div
                className="custom-overlay"
                style={{ fontSize: "14px", fontWeight: "bold", color: "#333", fontFamily: "'Sour Gummy', cursive" }}
              >
                SafetyBox
              </div>
            </div>
          </CustomOverlayMap>
        </Map>
      </Box>

      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          SafetyBox Status
        </Typography>
        <Box
          sx={{
            border: `1px solid ${theme.palette.divider}`,
            padding: 2,
            marginBottom: 2,
            borderRadius: 1,
            backgroundColor: theme.palette.background.paper,
            boxShadow: 1,
          }}
        >
          <Typography variant="subtitle1" color="text.primary" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
            Stolen or not
          </Typography>
          <Typography
            variant="h6"
            sx={{
              color: isStolen ? theme.palette.error.main : theme.palette.success.main,
              fontFamily: "'Sour Gummy', cursive",
            }}
          >
            {isStolen ? "Stolen" : "Safe"}
          </Typography>
        </Box>
        <DataGraph />
      </Box>

      <Dialog open={showPopup} onClose={() => handlePopupClose(false)}>
        <DialogTitle sx={{ fontFamily: "'Sour Gummy', cursive", fontSize: "18px" }}>
          Arrival confirmation
        </DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ fontFamily: "'Sour Gummy', cursive", fontSize: "18px" }}>
            The drone has arrived at its destination, do you want to proceed with authorisation?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => handlePopupClose(true)} color="primary" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
            Yes
          </Button>
          <Button onClick={() => handlePopupClose(false)} color="secondary" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
            No
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Dashboard;
