import React, { useEffect, useState } from "react";
import { Map, CustomOverlayMap } from "react-kakao-maps-sdk";
import DataGraph from "./DataGraph";
import { Box, Typography, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField, useTheme } from "@mui/material";

function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  const [location, setLocation] = useState(null);
  const [isStolen, setIsStolen] = useState(false);
  const [isArrived, setIsArrived] = useState(false); // 도착 여부
  const [showPopup, setShowPopup] = useState(false); // 팝업 표시 여부
  const [userData, setUserData] = useState(""); // 팝업에서 입력한 데이터

  const theme = useTheme();

  const destination = {
    lat: 37.55062087654126,
    lng: 127.07425390537487,
  };

  const haversineDistance = (coords1, coords2) => {
    const toRad = (x) => (x * Math.PI) / 180;
    const R = 6371; // 지구 반지름 (km)
    const dLat = toRad(coords2.lat - coords1.lat);
    const dLon = toRad(coords2.lng - coords1.lng);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(coords1.lat)) *
        Math.cos(toRad(coords2.lat)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c; // 거리 (km)
  };

  useEffect(() => {
    const fetchLocationData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-location", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          const newLocation = {
            lat: parseFloat(data.latitude),
            lng: parseFloat(data.longitude),
          };
          setLocation(newLocation);
          setIsStolen(data.isStolen);

          // 도착 여부 확인
          const distance = haversineDistance(newLocation, destination);
          if (distance < 0.1 && !isArrived) { // 0.1km 이내면 도착
            setIsArrived(true);
            setShowPopup(true);
          }
        } else {
          console.error("Failed to fetch location data");
        }
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    const intervalId = setInterval(fetchLocationData, 5000);

    return () => clearInterval(intervalId);
  }, [isArrived]);

  const handlePopupClose = (approved) => {
    setShowPopup(false);
    if (approved) {
      console.log("사용자가 승인했습니다.");
      console.log(`입력 데이터: ${userData}`);
      // API 호출 등의 추가 로직 실행
    } else {
      console.log("사용자가 승인을 거부했습니다.");
    }
    setUserData(""); // 팝업 닫을 때 입력 필드 초기화
  };

  if (!location) {
    return (
      <div className="dashboard-container" style={{ textAlign: "center" }}>
        <Typography variant="h4" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          Loading location... 😅
        </Typography>
      </div>
    );
  }

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        gap: 4,
        fontFamily: "'Sour Gummy', cursive",
        padding: 2,
        flexWrap: "wrap",
      }}
    >
      {/* 지도 섹션 */}
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          SafetyBox Location
        </Typography>
        <Map
          center={{ lat: location.lat, lng: location.lng }}
          level={3}
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          {/* Custom Overlay */}
          <CustomOverlayMap position={{ lat: location.lat, lng: location.lng }} zIndex={1}>
            <div style={{ position: "relative", display: "flex", flexDirection: "column", alignItems: "center" }}>
              <img
                src="/marker.png"
                alt="marker"
                style={{ width: "36px", height: "36px" }}
              />
              <div
                className="custom-overlay"
                style={{ fontSize: "14px", fontWeight: "bold", color: "#333", fontFamily: "'Sour Gummy', cursive" }}
              >
                SafetyBox
              </div>
            </div>
          </CustomOverlayMap>
        </Map>
      </Box>

      {/* 도난 여부 및 Sensor Data 섹션 */}
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          SafetyBox 상태
        </Typography>
        <Box
          sx={{
            border: `1px solid ${theme.palette.divider}`,
            padding: 2,
            marginBottom: 2,
            borderRadius: 1,
            backgroundColor: theme.palette.background.paper,
            boxShadow: 1,
          }}
        >
          <Typography variant="subtitle1" color="text.primary" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
            Stolen or not
          </Typography>
          <Typography
            variant="h6"
            sx={{
              color: isStolen ? theme.palette.error.main : theme.palette.success.main,
              fontFamily: "'Sour Gummy', cursive",
            }}
          >
            {isStolen ? "Stolen or not" : "Safe"}
          </Typography>
        </Box>
        <DataGraph />
      </Box>

      {/* 팝업 다이얼로그 */}
      <Dialog open={showPopup} onClose={() => handlePopupClose(false)}>
        <DialogTitle sx={{ fontWeight: "bold", fontFamily: "'Sour Gummy', cursive" }}>
          📍 도착 확인
        </DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ marginBottom: 2, fontFamily: "'Sour Gummy', cursive" }}>
            드론이 목적지에 도착했습니다. 데이터를 입력하거나 확인 후 승인을 진행하세요.
          </DialogContentText>
          <TextField
            fullWidth
            variant="outlined"
            label="확인 데이터 입력"
            value={userData}
            onChange={(e) => setUserData(e.target.value)}
            sx={{
              marginBottom: 2,
              fontFamily: "'Sour Gummy', cursive",
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => handlePopupClose(true)} variant="contained" color="primary">
            승인
          </Button>
          <Button onClick={() => handlePopupClose(false)} variant="outlined" color="secondary">
            취소
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Dashboard;
