import React, { useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';  // 데이터 레이블 플러그인

// Chart.js 플러그인 등록
Chart.register(...registerables);
Chart.register(ChartDataLabels); // 데이터 레이블 플러그인

function DataGraph() {
  const chartRef = useRef(null);

  useEffect(() => {
    const ctx = chartRef.current?.getContext('2d'); // null 체크 추가
    if (ctx) {
      const chart = new Chart(ctx, {
        type: 'line',
        data: {
          labels: ['10:00', '10:05', '10:10', '10:15', '10:20'], // 시간대 데이터
          datasets: [
            {
              label: '충격 센서 (g)',
              data: [0.2, 0.5, 1.0, 0.3, 0.4], // 충격 데이터 예시
              borderColor: 'rgba(255, 99, 132, 1)',
              backgroundColor: 'rgba(255, 99, 132, 0.2)',
              fill: true,
              pointRadius: 6,  // 데이터 포인트 크기 조정
              pointBackgroundColor: 'rgba(255, 99, 132, 1)',
              pointHoverRadius: 8, // 마우스 hover 시 데이터 포인트 크기
              borderWidth: 2,
              datalabels: {
                color: 'rgba(255, 99, 132, 1)',
                font: {
                  weight: 'bold',
                  size: 14,
                },
                align: 'top',
                offset: 10,
              },
            },
            {
              label: '온도 센서 (°C)',
              data: [20, 21, 22, 21, 20], // 온도 데이터 예시
              borderColor: 'rgba(54, 162, 235, 1)',
              backgroundColor: 'rgba(54, 162, 235, 0.2)',
              fill: true,
              pointRadius: 6,
              pointBackgroundColor: 'rgba(54, 162, 235, 1)',
              pointHoverRadius: 8,
              borderWidth: 2,
              datalabels: {
                color: 'rgba(54, 162, 235, 1)',
                font: {
                  weight: 'bold',
                  size: 14,
                },
                align: 'top',
                offset: 10,
              },
            },
          ],
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              position: 'top',
              labels: {
                font: {
                  size: 16, // 범례 글씨 크기 조정
                },
              },
            },
            datalabels: {
              color: 'black',  // 데이터 레이블 색상 설정
              font: {
                size: 16,
                weight: 'bold',
              },
            },
          },
          scales: {
            x: {
              ticks: {
                font: {
                  size: 14, // x축 글씨 크기
                },
              },
            },
            y: {
              ticks: {
                font: {
                  size: 14, // y축 글씨 크기
                },
              },
            },
          },
        },
      });

      return () => chart.destroy();
    }
  }, []);

  return (
    <div className="data-graph-container" style={{ position: 'relative', height: '400px', width: '100%' }}>
      <canvas ref={chartRef} />
    </div>
  );
}

export default DataGraph;
