import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Container, Divider } from '@mui/material';
import { styled } from '@mui/system'; // 새로운 스타일링 방법

// 스타일링을 위한 Box 컴포넌트 스타일 정의
const LogoBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  color: theme.palette.primary.contrastText,
}));

const LogoText = styled(Typography)(({ theme }) => ({
  marginLeft: theme.spacing(1),
  fontSize: '20px',
  fontWeight: 'bold',
}));

const ButtonContainer = styled(Box)(({ theme }) => ({
  marginLeft: 'auto',
  display: 'flex',
  gap: theme.spacing(2),
}));

const StyledButton = styled(Button)(({ theme }) => ({
  color: theme.palette.primary.contrastText,
  '&:hover': {
    backgroundColor: theme.palette.primary.light,
  },
}));

const NavigationBar = ({ onSafetyBoxClick, onCCTVClick, activePage }) => {
  return (
    <div>
      <AppBar position="sticky" color="primary">
        <Toolbar>
          <LogoBox>
            <img src="/logo.png" alt="Logo" width={40} height={40} />
            <LogoText variant="h6">IoT Dashboard</LogoText>
          </LogoBox>

          <ButtonContainer>
            <StyledButton onClick={onSafetyBoxClick}>
              Safety Box Information
            </StyledButton>
            <StyledButton onClick={onCCTVClick}>
              Checking CCTV
            </StyledButton>
          </ButtonContainer>
        </Toolbar>
      </AppBar>

      <Container>
        <Box p={3}>
          <Typography variant="h6" color="textSecondary">
            Current Page: {activePage}
          </Typography>
          <Divider sx={{ my: 2 }} />
        </Box>
      </Container>
    </div>
  );
};

export default NavigationBar;
