import React, { useEffect, useState } from "react";
import { Map, CustomOverlayMap } from "react-kakao-maps-sdk";
import DataGraph from "./DataGraph";
import { Box, Typography, Button, Dialog, DialogActions, DialogContent, DialogTitle, useTheme } from "@mui/material";
import sendCommand from "./sendCommand"; // `sendCommand`Î•º Î≥ÑÎèÑ ÌååÏùºÎ°ú Î∂ÑÎ¶¨

function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  const [location, setLocation] = useState(null);
  const [isStolen, setIsStolen] = useState(false);
  const [open, setOpen] = useState(false); // ÌåùÏóÖ Ïó¥Î¶º ÏÉÅÌÉú
  const theme = useTheme();

  useEffect(() => {
    const fetchLocationData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-location", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setLocation({
            lat: parseFloat(data.latitude),
            lng: parseFloat(data.longitude),
          });

          setIsStolen(data.isStolen);
        } else {
          console.error("Failed to fetch location data");
        }
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    const intervalId = setInterval(fetchLocationData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  const handleOpenDialog = () => {
    setOpen(true);
  };

  const handleCloseDialog = () => {
    setOpen(false);
  };

  const handleApprove = async () => {
    try {
      // 1. Î®ºÏ†Ä 'unlocked' Î™ÖÎ†π Î≥¥ÎÇ¥Í∏∞
      await sendCommand("unlocked");
      alert("Door unlocked successfully!");

      // 2. 1Î∂Ñ Îí§Ïóê 'locked' Î™ÖÎ†π ÏûêÎèô Ï†ÑÏÜ°
      setTimeout(async () => {
        try {
          await sendCommand("locked");
          alert("Door locked automatically after 1 minute!");
        } catch (error) {
          console.error("Failed to lock door automatically:", error);
          alert("Error locking door automatically.");
        }
      }, 60000); // 1Î∂Ñ = 60000ms
    } catch (error) {
      console.error("Failed to unlock door:", error);
      alert("Error unlocking door.");
    } finally {
      setOpen(false); // ÌåùÏóÖ Îã´Í∏∞
    }
  };

  if (!location) {
    return (
      <div className="dashboard-container" style={{ textAlign: "center" }}>
        <Typography variant="h4" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          Loading location... üòÖ
        </Typography>
      </div>
    );
  }

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        gap: 4,
        padding: 2,
        flexWrap: "wrap",
      }}
    >
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom>
          SafetyBox Location
        </Typography>
        <Map
          center={{ lat: location.lat, lng: location.lng }}
          level={3}
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          <CustomOverlayMap position={{ lat: location.lat, lng: location.lng }} zIndex={1}>
            <div style={{ position: "relative", display: "flex", flexDirection: "column", alignItems: "center" }}>
              <img
                src="/marker.png"
                alt="marker"
                style={{ width: "36px", height: "36px" }}
              />
              <div
                className="custom-overlay"
                style={{ fontSize: "14px", fontWeight: "bold", color: "#333" }}
              >
                SafetyBox
              </div>
            </div>
          </CustomOverlayMap>
        </Map>
      </Box>

      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom>
          SafetyBox ÏÉÅÌÉú
        </Typography>
        <Button variant="contained" color="primary" onClick={handleOpenDialog}>
          Approve Unlock
        </Button>
        <Dialog open={open} onClose={handleCloseDialog}>
          <DialogTitle>Approve Door Unlock</DialogTitle>
          <DialogContent>
            <Typography>Do you want to unlock the door?</Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog} color="secondary">
              No
            </Button>
            <Button onClick={handleApprove} color="primary">
              Yes
            </Button>
          </DialogActions>
        </Dialog>
        <DataGraph />
      </Box>
    </Box>
  );
}

export default Dashboard;
