import React, { useState } from 'react';
import { Map, MapMarker } from "react-kakao-maps-sdk"; // MapMarker로 변경

function Dashboard() {
  const containerStyle = {
    width: '1000px', // 크기 수정
    height: '600px',
  };

  // 기본 위치 설정
  const [location] = useState({
    lat: 37.5665, // 서울의 기본 위도
    lng: 126.9780, // 서울의 기본 경도
  });

  return (
    <div className="dashboard-container">
      <div className="map-section">
        <h2>배송 위치</h2>
        <Map
          center={{ lat: location.lat, lng: location.lng }} // 기본 lat, lng
          level={3} // 지도 확대 레벨
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa" // 카카오 API 키
        >
          <MapMarker position={{ lat: location.lat, lng: location.lng }} /> {/* MapMarker로 변경 */}
        </Map>
      </div>
      <div className="data-section">
        <h2>데이터 시각화</h2>
        <p>데이터 그래프가 여기에 표시됩니다.</p>
      </div>
    </div>
  );
}

export default Dashboard;
