import React, { useEffect, useState } from "react";
import { Map, CustomOverlayMap } from "react-kakao-maps-sdk";
import DataGraph from "./DataGraph";
import { Box, Typography } from "@mui/material"; // useTheme 제거

function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  const [location, setLocation] = useState(null);
  const [isStolen, setIsStolen] = useState(false); // 도난 여부 상태 추가

  useEffect(() => {
    const fetchLocationData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-location", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setLocation({
            lat: parseFloat(data.latitude),
            lng: parseFloat(data.longitude),
          });

          // 도난 여부 정보 예시로 설정
          setIsStolen(data.isStolen); // 서버에서 도난 여부 값을 받아서 설정
        } else {
          console.error("Failed to fetch location data");
        }
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    const intervalId = setInterval(fetchLocationData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  if (!location) {
    return (
      <div className="dashboard-container" style={{ textAlign: "center" }}>
        <h2>Loading location... 😅</h2>
      </div>
    );
  }

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        gap: 4,
        fontFamily: "'Roboto', sans-serif",
        padding: 2,
        flexWrap: "wrap",
      }}
    >
      {/* 지도 섹션 */}
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom>
          SafetyBox Location
        </Typography>
        <Map
          center={{ lat: location.lat, lng: location.lng }}
          level={3}
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          {/* Custom Overlay */}
          <CustomOverlayMap position={{ lat: location.lat, lng: location.lng }} zIndex={1}>
            <div style={{ position: "relative", display: "flex", flexDirection: "column", alignItems: "center" }}>
              <img
                src="/marker.png"
                alt="marker"
                style={{ width: "36px", height: "36px" }}
              />
              <div className="custom-overlay" style={{ fontSize: "14px", fontWeight: "bold", color: "#333" }}>
                SafetyBox
              </div>
            </div>
          </CustomOverlayMap>
        </Map>
      </Box>

      {/* 도난 여부 및 Sensor Data 섹션 */}
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom>
          SafetyBox 상태
        </Typography>
        <Box
          sx={{
            border: "1px solid #ccc",
            padding: 2,
            marginBottom: 2,
            borderRadius: 1,
            backgroundColor: "#f3f4f6",
            boxShadow: 1,
          }}
        >
          <Typography variant="subtitle1" color="text.primary">
            도난 여부
          </Typography>
          <Typography variant="h6" sx={{ color: isStolen ? "#e74c3c" : "#2ecc71" }}>
            {isStolen ? "도난됨" : "안전"}
          </Typography>
        </Box>

        <Typography variant="h6" align="center" gutterBottom>
          Sensor Data
        </Typography>
        <DataGraph />
      </Box>
    </Box>
  );
}

export default Dashboard;
