import * as React from 'react';
import { axisClasses } from '@mui/x-charts/ChartsAxis';
import { BarChart } from '@mui/x-charts/BarChart';

const dataset = [
  { time: '10:00', shock: 0.2, temperature: 20 },
  { time: '10:05', shock: 0.5, temperature: 21 },
  { time: '10:10', shock: 1.0, temperature: 22 },
  { time: '10:15', shock: 0.3, temperature: 21 },
  { time: '10:20', shock: 0.4, temperature: 20 },
];

const valueFormatter = (value: number | null) => `${value} g`;  // 충격 값 포맷

export default function FormatterDemoNoSnap() {
  return (
    <BarChart
      dataset={dataset}  // 데이터셋 설정
      height={400}  // 차트 높이 설정
      grid={{ horizontal: true }}  // 수평 그리드 설정
      sx={{
        [`& .${axisClasses.left} .${axisClasses.label}`]: {
          transform: 'translateX(-10px)',  // X축 라벨 위치 조정
        },
      }}
      xAxis={[
        {
          scaleType: 'band',  // 범주형 X축 사용
          dataKey: 'time',  // X축에 사용할 데이터 키
          valueFormatter: (time, context) =>
            context.location === 'tick' ? `${time}` : time,  // 시간 포맷
        },
      ]}
      yAxis={[
        { label: 'Shock (g)' },  // 충격 센서 Y축 라벨
        { label: 'Temperature (°C)' },  // 온도 센서 Y축 라벨
      ]}
      series={[
        {
          dataKey: 'shock',  // 'shock' 데이터 사용
          label: 'Shock Sensor',  // 시리즈 라벨
          valueFormatter,  // 값 포맷 설정
        },
        {
          dataKey: 'temperature',  // 'temperature' 데이터 사용
          label: 'Temperature Sensor',  // 시리즈 라벨
          valueFormatter,  // 값 포맷 설정
        },
      ]}
    />
  );
}
