import React from 'react';
import { 
  NavigationMenu, 
  NavigationMenuItem, 
  NavigationMenuList, 
  NavigationMenuTrigger 
} from "@/components/ui/navigation-menu";
import { 
  Card, 
  CardHeader, 
  CardTitle 
} from "@/components/ui/card";
import { 
  ShieldCheck, 
  Camera 
} from "lucide-react";

const NavigationBar = ({ onSafetyBoxClick, onCCTVClick, activePage }) => {
  return (
    <Card className="fixed top-4 right-4 w-auto z-50 shadow-lg">
      <CardHeader className="p-4 pb-0">
        <CardTitle className="text-sm text-gray-600 mb-2">
          Dashboard: {activePage}
        </CardTitle>
        <NavigationMenu>
          <NavigationMenuList className="flex space-x-2">
            <NavigationMenuItem>
              <NavigationMenuTrigger 
                onClick={onSafetyBoxClick} 
                className="flex items-center gap-2 bg-blue-50 hover:bg-blue-100 text-blue-700 px-3 py-2 rounded-md transition-colors"
              >
                <ShieldCheck size={18} />
                Safety Box
              </NavigationMenuTrigger>
            </NavigationMenuItem>
            <NavigationMenuItem>
              <NavigationMenuTrigger 
                onClick={onCCTVClick} 
                className="flex items-center gap-2 bg-green-50 hover:bg-green-100 text-green-700 px-3 py-2 rounded-md transition-colors"
              >
                <Camera size={18} />
                CCTV Check
              </NavigationMenuTrigger>
            </NavigationMenuItem>
          </NavigationMenuList>
        </NavigationMenu>
      </CardHeader>
    </Card>
  );
};

export default NavigationBar;
