import React, { useEffect, useState } from "react";
import { Map, MapMarker } from "react-kakao-maps-sdk";
import DataGraph from "./DataGraph";

function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  const [location, setLocation] = useState(null);

  useEffect(() => {
    const fetchLocationData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-location", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setLocation({
            lat: parseFloat(data.latitude),
            lng: parseFloat(data.longitude),
          });
        } else {
          console.error("Failed to fetch location data");
        }
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    const intervalId = setInterval(fetchLocationData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  if (!location) {
    return (
      <div className="dashboard-container">
        <h2>위치 로딩 중...</h2>
      </div>
    );
  }

  return (
    <div
      className="dashboard-container"
      style={{ display: "flex", flexDirection: "row", gap: "20px" }}
    >
      {/* 지도 섹션 */}
      <div className="map-section" style={{ flex: 1 }}>
        <h2>SafetyBox Location</h2>
        <Map
          center={{ lat: location.lat, lng: location.lng }}
          level={3}
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          {/* 커스텀 마커 */}
          <MapMarker
            position={{ lat: location.lat, lng: location.lng }}
            image={{
              src: "/marker.png", // public 폴더에서 이미지 경로
              size: {
                width: 36,
                height: 36, // 이미지 크기 조정
              },
            }}
          >
            {/* 마커 위 텍스트 */}
            <div
              style={{
                position: "absolute",
                top: "-50px", // 텍스트 위치 조정
                left: "-25px",
                background: "#fff",
                border: "2px solid #3498db",
                borderRadius: "15px",
                padding: "10px 15px",
                boxShadow: "0px 2px 5px rgba(0, 0, 0, 0.3)",
                textAlign: "center",
                color: "#2c3e50",
                fontWeight: "bold",
                fontSize: "14px",
                whiteSpace: "nowrap",
              }}
            >
              SafetyBox
            </div>
          </MapMarker>
        </Map>
      </div>

      {/* 데이터 섹션 */}
      <div className="data-section" style={{ flex: 1 }}>
        <h2>위치 정보</h2>
        <p>위도: {location.lat}</p>
        <p>경도: {location.lng}</p>
        <h2>Sensor Data</h2>
        <DataGraph />
      </div>
    </div>
  );
}

export default Dashboard;
