import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Box, Paper, Checkbox, FormControlLabel } from '@mui/material';

function DataGraph() {
  const [isResponsive, setIsResponsive] = useState(true); // 반응형 차트 기본값 true

  // 센서 데이터
  const data = [
    { time: '10:00', shock: 0.2, temperature: 20 },
    { time: '10:05', shock: 0.5, temperature: 21 },
    { time: '10:10', shock: 1.0, temperature: 22 },
    { time: '10:15', shock: 0.3, temperature: 21 },
    { time: '10:20', shock: 0.4, temperature: 20 },
  ];

  return (
    <Box sx={{ width: '100%' }}>
      {/* 반응형 차트 사용 여부를 토글하는 체크박스 */}
      <FormControlLabel
        checked={isResponsive}
        control={
          <Checkbox onChange={(event) => setIsResponsive(event.target.checked)} />
        }
        label="Use responsive container"
        labelPlacement="end"
      />

      <Paper sx={{ width: '100%', height: 400 }} elevation={3}>
        {/* 반응형 차트 컨테이너 */}
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={data}>
            {/* 격자선 추가 */}
            <CartesianGrid strokeDasharray="3 3" />
            {/* X축 데이터 */}
            <XAxis dataKey="time" />
            {/* Y축 데이터 */}
            <YAxis />
            {/* 툴팁 */}
            <Tooltip />
            {/* 범례 */}
            <Legend />
            {/* 충격 센서 데이터 라인 */}
            <Line type="monotone" dataKey="shock" stroke="#ff6384" />
            {/* 온도 센서 데이터 라인 */}
            <Line type="monotone" dataKey="temperature" stroke="#36a2eb" />
          </LineChart>
        </ResponsiveContainer>
      </Paper>
    </Box>
  );
}

export default DataGraph;
