import React, { useEffect, useState } from "react";
import { Map, CustomOverlayMap } from "react-kakao-maps-sdk";
import DataGraph from "./DataGraph";

function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  const [location, setLocation] = useState(null);
  const [isStolen, setIsStolen] = useState(false); // 도난 여부 상태 추가

  useEffect(() => {
    const fetchLocationData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-location", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setLocation({
            lat: parseFloat(data.latitude),
            lng: parseFloat(data.longitude),
          });

          // 도난 여부 정보 예시로 설정
          setIsStolen(data.isStolen); // 서버에서 도난 여부 값을 받아서 설정
        } else {
          console.error("Failed to fetch location data");
        }
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    const intervalId = setInterval(fetchLocationData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  if (!location) {
    return (
      <div className="dashboard-container">
        <h2>Loading location... 😅</h2>
      </div>
    );
  }

  return (
    <div
      className="dashboard-container"
      style={{ display: "flex", flexDirection: "row", gap: "20px", fontFamily: "'Roboto', sans-serif" }} // 폰트 변경
    >
      {/* 지도 섹션 */}
      <div className="map-section" style={{ flex: 1 }}>
        <h2>SafetyBox Location</h2>
        <Map
          center={{ lat: location.lat, lng: location.lng }}
          level={3}
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          {/* Custom Overlay */}
          <CustomOverlayMap
            position={{ lat: location.lat, lng: location.lng }}
            zIndex={1}
          >
            {/* 마커 이미지 */}
            <div
              style={{
                position: "relative",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <img
                src="/marker.png" // public 폴더의 마커 이미지
                alt="marker"
                style={{ width: "36px", height: "36px" }}
              />
              {/* 커스텀 텍스트 */}
              <div className="custom-overlay">
                SafetyBox
              </div>
            </div>
          </CustomOverlayMap>
        </Map>
      </div>

      {/* 도난 여부 섹션 */}
      <div className="data-section" style={{ flex: 1 }}>
        <h2>SafetyBox 상태</h2>
        <div
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            marginBottom: "10px",
            borderRadius: "8px", // 모서리 둥글게
            background: "#f3f4f6", // 밝은 배경
            boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)", // 부드러운 그림자
            fontFamily: "'Poppins', sans-serif", // 폰트 변경
          }}
        >
          <h3 style={{ fontSize: "18px", color: "#333" }}>도난 여부</h3>
          <p style={{ fontSize: "16px", color: isStolen ? "#e74c3c" : "#2ecc71" }}>
            {isStolen ? "도난됨" : "안전"} {/* 도난 상태에 따라 색상 변경 */}
          </p>
        </div>

        <h2>Sensor Data</h2>
        <DataGraph />
      </div>
    </div>
  );
}

export default Dashboard;
