import React, { useState, useEffect } from 'react';
import { Button, Typography, CircularProgress, Box } from '@mui/material'; // Material-UI 컴포넌트 추가

const CCTVPage = () => {
  const [lockTimeout, setLockTimeout] = useState(null); // CCTV Live Stream
  const [responseMessage, setResponseMessage] = useState(""); // 응답 메시지 상태 저장
  const [loading, setLoading] = useState(false); // 로딩 상태 추가
  const [recordings, setRecordings] = useState([]); // 녹화본 목록 상태
  const [selectedRecording, setSelectedRecording] = useState(null); // 선택된 녹화본 상태

  // 녹화본 목록을 서버에서 받아오는 함수
  const fetchRecordings = async () => {
    try {
      const response = await fetch('http://localhost:5000/recordings'); // 서버 주소
      if (!response.ok) {
        throw new Error('Failed to fetch recordings');
      }
      const data = await response.json();
      setRecordings(data.recordings); // 녹화본 목록 설정
    } catch (error) {
      console.error('Error fetching recordings:', error);
    }
  };

  // 컴포넌트 마운트 시 녹화본 목록을 가져옴
  useEffect(() => {
    fetchRecordings();
  }, []);

  const sendCommand = async (command) => {
    try {
      setLoading(true); // 요청 시작 시 로딩 상태 활성화
      const timestamp = new Date().toISOString();

      const commandData = {
        "m2m:cin": {
          "rn": `command_${timestamp.replace(/[:.]/g, '_')}`,
          "con": {
            "command": command,
            "timestamp": timestamp,
          },
        },
      };

      // Flask 서버로 POST 요청
      const response = await fetch('http://localhost:5000/send-command', { // Flask 서버 주소
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(commandData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server error: ${errorText}`);
      }

      // 응답 JSON 처리
      const data = await response.json();
      setResponseMessage(data.message || "Command executed successfully");
      alert(`${command} command sent successfully: ${data.message}`);
    } catch (error) {
      console.error('Error sending command:', error);
      setResponseMessage(`Error: ${error.message}`);
      alert(`An error occurred: ${error.message}`);
    } finally {
      setLoading(false); // 요청 완료 후 로딩 상태 비활성화
    }
  };

  const handleDoorLock = () => {
    sendCommand('unlocked');

    // 기존 타임아웃 클리어
    if (lockTimeout) {
      clearTimeout(lockTimeout);
    }

    // 1분 후 도어 잠금 명령
    const newTimeout = setTimeout(() => {
      sendCommand('locked');
      setLockTimeout(null);
    }, 600); // 1분 = 60000ms

    setLockTimeout(newTimeout);
  };

  const handleRecordingSelect = (recording) => {
    setSelectedRecording(recording); // 선택된 녹화본 설정
  };

  return (
    <div className="cctv-container">
      <div className="cctv-stream">
        <Typography variant="h5" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          CCTV Live Stream
        </Typography>
        <div className="video-placeholder">
          {/* 비디오 파일을 public/videos 폴더에서 참조 */}
          <video width="100%" controls autoPlay loop>
            <source src="/videos/your-video.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      </div>

      {/* 녹화본 선택 영역 */}
      <Box
        sx={{
          width: "100%",
          padding: 3,
          marginTop: 4,
          backgroundColor: "#f5f5f5",
          borderRadius: 2,
          boxShadow: 3,
        }}
      >
        <Typography variant="h6" align="center" sx={{ fontFamily: "'Sour Gummy', cursive", marginBottom: 2 }}>
          Select a Recording
        </Typography>
        <div className="recordings-list" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          {recordings.length > 0 ? (
            recordings.map((recording, index) => (
              <Button
                key={index}
                variant="contained"
                onClick={() => handleRecordingSelect(recording)}
                sx={{
                  marginBottom: 1,
                  backgroundColor: '#1976d2',
                  '&:hover': {
                    backgroundColor: '#1565c0',
                  },
                }}
              >
                {`Recording ${index + 1} - ${new Date(recording.date).toLocaleDateString()}`}
              </Button>
            ))
          ) : (
            <Typography variant="body1" color="textSecondary">
              No recordings available
            </Typography>
          )}
        </div>
        {selectedRecording && (
          <div className="selected-recording" style={{ marginTop: '20px' }}>
            <Typography variant="h6" align="center" sx={{ fontFamily: "'Sour Gummy', cursive", marginBottom: 2 }}>
              Playing Selected Recording
            </Typography>
            <video width="100%" controls autoPlay>
              <source src={`/videos/${selectedRecording.fileName}`} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        )}
      </Box>

      <div className="door-control" style={{ marginTop: '20px' }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleDoorLock}
          size="large"
          fullWidth
          sx={{
            backgroundColor: '#d32f2f', // 빨간색 배경
            '&:hover': {
              backgroundColor: '#c62828', // 호버 시 빨간색
            },
            padding: '12px', // 버튼 안쪽 여백
            fontSize: '18px', // 글자 크기
            fontWeight: '600', // 글자 두께
            fontFamily: "'Sour Gummy', cursive"
          }}
          disabled={loading} // 로딩 중일 때 버튼 비활성화
        >
          {loading ? <CircularProgress size={24} sx={{ color: 'white' }} /> : 'Unlock Door'}
        </Button>
        {responseMessage && (
          <Typography variant="body1" color="textSecondary" sx={{ marginTop: '20px' }} >
            {responseMessage}
          </Typography>
        )}
      </div>
    </div>
  );
};

export default CCTVPage;
