import React from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { Box, Typography, useTheme } from "@mui/material";

const data = [
  { time: "10:00", shock: 0.2, temp: 20 },
  { time: "10:05", shock: 0.5, temp: 21 },
  { time: "10:10", shock: 1.0, temp: 22 },
  { time: "10:15", shock: 0.3, temp: 21 },
  { time: "10:20", shock: 0.4, temp: 20 },
];

function DataGraph() {
  const theme = useTheme();

  return (
    <Box
      sx={{
        width: "100%",
        height: 400,
        padding: 2,
        boxShadow: theme.shadows[3],
        borderRadius: theme.shape.borderRadius,
        backgroundColor: theme.palette.background.paper,
      }}
    >
      <Typography variant="h6" align="center" gutterBottom>
        Sensor data graphs
      </Typography>
      <ResponsiveContainer width="90%" height="100%">
        <LineChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 40 }}> {/* 여유 공간 더 추가 */}
          <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
          <XAxis
            dataKey="time"
            tick={{ fontSize: 12, fill: theme.palette.text.primary }}
            label={{
              value: "Time",
              position: "insideBottom", // 레이블을 안쪽에 두고
              offset: 0, // 더 넓은 간격 확보
            }}
            tickMargin={10}  // Tick과 레이블 사이에 추가적인 여백을 둠
          />
          <YAxis
            tick={{ fontSize: 12, fill: theme.palette.text.primary }}
            label={{ value: "value", angle: -90, position: "insideLeft", offset: 10 }}
          />
          <Tooltip contentStyle={{ backgroundColor: theme.palette.background.paper, border: `1px solid ${theme.palette.divider}` }} />
          <Legend
            verticalAlign="top"
            height={36}
            wrapperStyle={{ fontSize: 12, color: theme.palette.text.secondary }}
          />
          <Line
            type="monotone"
            dataKey="shock"
            name="Shock sensor (g)"
            stroke={theme.palette.error.main}
            activeDot={{ r: 8 }}
            strokeWidth={2}
            dot={{ r: 5, fill: theme.palette.error.main }}
          />
          <Line
            type="monotone"
            dataKey="temp"
            name="Temperature sensors (°C)"
            stroke={theme.palette.primary.main}
            activeDot={{ r: 8 }}
            strokeWidth={2}
            dot={{ r: 5, fill: theme.palette.primary.main }}
          />
        </LineChart>
      </ResponsiveContainer>
    </Box>
  );
}

export default DataGraph;
