import React, { useEffect, useState } from 'react';
import { Map, MapMarker } from "react-kakao-maps-sdk"; // MapMarker로 변경
import DataGraph from './DataGraph';

function Dashboard() {
  const containerStyle = {
    width: '1000%',
    height: '600px',
  };

  const [location, setLocation] = useState({
    lat: 37.7749, // 기본 위도 (서버로부터 받아오기 전 기본값)
    lng: 126.9780, // 기본 경도 (서버로부터 받아오기 전 기본값)
  });


  return (
    <div className="dashboard-container">
      <div className="map-section">
        <h2>배송 위치</h2>
        <Map
          center={{ lat: 33.450701, lng: 126.570667 }}
          level={3} // 지도 확대 레벨
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa" // 카카오 API 키
        >
          <MapMarker position={{ lat: location.lat, lng: location.lng }} /> {/* MapMarker로 변경 */}
        </Map>
      </div>
      <div className="data-section">
        <h2>데이터 시각화</h2>
        <DataGraph />
      </div>
    </div>
  );
}

export default Dashboard;
