import React, { useEffect, useState } from "react";
import { Map, CustomOverlayMap } from "react-kakao-maps-sdk"; // 카카오 맵 라이브러리
import DataGraph from "./DataGraph"; // DataGraph 컴포넌트 임포트
import { Box, Typography, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, useTheme, TextField, Grid2 } from "@mui/material"; // MUI 라이브러리 임포트

// Dashboard 컴포넌트 정의
function Dashboard() {
  const containerStyle = {
    width: "100%",
    height: "600px",
  };

  // 상태 관리
  const [location, setLocation] = useState(null); // 위치 정보 상태
  const [isStolen, setIsStolen] = useState(false); // 도난 여부 상태
  const [isArrived, setIsArrived] = useState(false); // 도착 여부 상태
  const [showPopup, setShowPopup] = useState(false); // 팝업 표시 여부 상태
  const [chatMessage, setChatMessage] = useState(""); // 채팅 메시지 상태
  const [chatHistory, setChatHistory] = useState([]); // 채팅 기록 상태
  const [isFetching, setIsFetching] = useState(true); // 위치 데이터 페칭 중 여부 상태

  const theme = useTheme(); // 테마 사용

  const userName = "PP-P"; // 사용자 이름 (예시)

  // 하버사인 거리 계산 함수
  const haversineDistance = (coords1, coords2) => {
    const toRad = (x) => (x * Math.PI) / 180; // 도를 라디안으로 변환
    const R = 6371; // 지구 반지름 (km)
    const dLat = toRad(coords2.lat - coords1.lat); // 위도 차이 계산
    const dLon = toRad(coords2.lng - coords1.lng); // 경도 차이 계산
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRad(coords1.lat)) *
        Math.cos(toRad(coords2.lat)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2); // 하버사인 공식
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a)); // 거리 계산
    return R * c; // 거리 (km)
  };

  // 컴포넌트가 렌더링될 때 위치 데이터 fetch
  useEffect(() => {

    if (!isFetching) return; // 위치를 더 이상 가져오지 않음

    const destination = { lat: 37.7749, lng: -122.4194 }; // 도착지 정의 (샌프란시스코 예시)

    // 위치 데이터 fetch 함수
    const fetchLocationData = async () => {
      try {
        const response = await fetch("http://localhost:5000/get-location", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          const newLocation = {
            lat: parseFloat(data.latitude),
            lng: parseFloat(data.longitude),
          };
          setLocation(newLocation); // 위치 업데이트
          setIsStolen(data.isStolen); // 도난 여부 업데이트

          // 도착 여부 확인
          const distance = haversineDistance(newLocation, destination);
          if (distance < 0.1 && !isArrived) { // 0.1km 이내면 도착
            setIsArrived(true);
            setShowPopup(true); // 팝업 표시
          }
        } else {
          console.error("Failed to fetch location data");
        }
      } catch (error) {
        console.error("Error fetching location:", error);
      }
    };

    const intervalId = setInterval(fetchLocationData, 5000); // 5초마다 위치 데이터 fetch

    return () => clearInterval(intervalId); // 컴포넌트 unmount 시 인터벌 정리
  }, [isFetching, isArrived]); // 상태가 변경될 때마다 useEffect 실행

  // 팝업 닫기 및 승인 처리 함수
  const handlePopupClose = (approved) => {
    setShowPopup(false);
    if (approved) {
      console.log("User approved.");
      setIsFetching(false); // 위치 fetch 중단
      // 추가 로직 (예: API 호출)
    } else {
      console.log("User declined.");
      setIsFetching(true); // 위치 fetch 재개
    }
  };

  // 위치 fetch 시작 함수
  const handleStartFetching = () => {
    setIsFetching(true); // 위치 fetch 시작
  };

  // 채팅 메시지 전송 함수
  const handleSendMessage = () => {
    if (chatMessage.trim()) {
      setChatHistory([...chatHistory, { user: userName, message: chatMessage }]);
      setChatMessage(""); // 메시지 입력창 초기화
    }
  };

  // 위치가 없을 경우 로딩 화면 표시
  if (!location) {
    return (
      <div className="dashboard-container" style={{ textAlign: "center" }}>
        <Typography variant="h4" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          Loading location... 😅
        </Typography>
      </div>
    );
  }

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        gap: 4,
        fontFamily: "'Sour Gummy', cursive",
        padding: 2,
        flexWrap: "wrap",
      }}
    >
      {/* 지도 섹션 */}
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <span>SafetyBox Location</span>
          <Button
            variant="contained"
            size="small"
            onClick={handleStartFetching}
            sx={{ fontFamily: "'Sour Gummy', cursive" }}
          >
            Refresh Location
          </Button>
        </Typography>
        <Map
          center={{ lat: location.lat, lng: location.lng }} // 현재 위치로 지도 설정
          level={3} // 지도 레벨 설정
          style={containerStyle}
          appKey="27def025665d22e2a866f398cfb7a3aa"
        >
          {/* 위치에 커스텀 오버레이 표시 */}
          <CustomOverlayMap position={{ lat: location.lat, lng: location.lng }} zIndex={1}>
            <div style={{ position: "relative", display: "flex", flexDirection: "column", alignItems: "center" }}>
              <img
                src="/marker.png"
                alt="marker"
                style={{ width: "36px", height: "36px" }}
              />
              <div
                className="custom-overlay"
                style={{ fontSize: "14px", fontWeight: "bold", color: "#333", fontFamily: "'Sour Gummy', cursive" }}
              >
                SafetyBox
              </div>
            </div>
          </CustomOverlayMap>
        </Map>
      </Box>

      {/* 도난 여부 및 센서 데이터 섹션 */}
      <Box
        sx={{
          flex: 1,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" gutterBottom sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          SafetyBox Status
        </Typography>
        <Box
          sx={{
            border: `1px solid ${theme.palette.divider}`,
            padding: 2,
            marginBottom: 2,
            borderRadius: 1,
            backgroundColor: theme.palette.background.paper,
            boxShadow: 1,
          }}
        >
          <Typography variant="subtitle1" color="text.primary" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
            Stolen or not
          </Typography>
          <Typography
            variant="h6"
            sx={{
              color: isStolen ? theme.palette.error.main : theme.palette.success.main,
              fontFamily: "'Sour Gummy', cursive",
            }}
          >
            {isStolen ? "Stolen" : "Safe"}
          </Typography>
        </Box>
        <DataGraph /> {/* 센서 데이터 그래프 컴포넌트 */}
      </Box>

      {/* 도착 확인 팝업 */}
      <Dialog open={showPopup} onClose={() => handlePopupClose(false)}>
        <DialogTitle sx={{ fontFamily: "'Sour Gummy', cursive", fontSize: '18px' }}>
          Arrival confirmation
        </DialogTitle>
        <DialogContent>
          <DialogContentText sx={{ fontFamily: "'Sour Gummy', cursive", fontSize: '16px' }}>
            The package has arrived at the destination. Would you like to confirm this?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => handlePopupClose(false)} sx={{ fontFamily: "'Sour Gummy', cursive" }}>
            Cancel
          </Button>
          <Button
            onClick={() => handlePopupClose(true)}
            sx={{ fontFamily: "'Sour Gummy', cursive", color: "green" }}
          >
            Confirm
          </Button>
        </DialogActions>
      </Dialog>

      {/* 채팅 섹션 */}
      <Box
        sx={{
          width: "100%",
          maxWidth: 400,
          padding: 2,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h6" align="center" sx={{ fontFamily: "'Sour Gummy', cursive" }}>
          Chat
        </Typography>
        <Box
          sx={{
            height: 300,
            overflowY: "scroll",
            border: "1px solid #ddd",
            padding: 2,
            borderRadius: 1,
            backgroundColor: "#f7f7f7",
            marginBottom: 2,
          }}
        >
          {chatHistory.map((chat, index) => (
            <Typography
              key={index}
              sx={{
                fontFamily: "'Sour Gummy', cursive",
                color: "#333",
                fontWeight: "bold",
                marginBottom: 1,
              }}
            >
              <strong>{chat.user}</strong>: {chat.message}
            </Typography>
          ))}
        </Box>
        <TextField
          label="Your message"
          variant="outlined"
          value={chatMessage}
          onChange={(e) => setChatMessage(e.target.value)}
          fullWidth
          sx={{
            fontFamily: "'Sour Gummy', cursive",
            marginBottom: 2,
          }}
        />
        <Button
          variant="contained"
          onClick={handleSendMessage}
          fullWidth
          sx={{
            fontFamily: "'Sour Gummy', cursive",
            backgroundColor: theme.palette.primary.main,
          }}
        >
          Send
        </Button>
      </Box>
    </Box>
  );
}

export default Dashboard;
