import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";

function DataGraph() {
  const [isResponsive, setIsResponsive] = useState(true);

  // 시간 데이터
  const data = [
    { time: '10:00', shock: 0.2, temperature: 20 },
    { time: '10:05', shock: 0.5, temperature: 21 },
    { time: '10:10', shock: 1.0, temperature: 22 },
    { time: '10:15', shock: 0.3, temperature: 21 },
    { time: '10:20', shock: 0.4, temperature: 20 },
  ];

  return (
    <div className="w-full p-4">
      <div className="flex items-center space-x-2 mb-4">
        <Checkbox 
          id="responsive-toggle"
          checked={isResponsive}
          onCheckedChange={setIsResponsive}
        />
        <Label htmlFor="responsive-toggle">Use responsive container</Label>
      </div>

      <Card className="w-full h-[400px]">
        <CardContent className="h-full p-4">
          {isResponsive ? (
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={data}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="time" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="shock" stroke="#ff6384" name="Shock" />
                <Line type="monotone" dataKey="temperature" stroke="#36a2eb" name="Temperature" />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <LineChart width={600} height={300} data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="shock" stroke="#ff6384" name="Shock" />
              <Line type="monotone" dataKey="temperature" stroke="#36a2eb" name="Temperature" />
            </LineChart>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default DataGraph;
