/* global kakao */
import React, { useEffect, useState } from 'react';
import DataGraph from './DataGraph';
import openBoxImg from './assets/open-box.png'; // 열림 상태 이미지
import closedBoxImg from './assets/closed-box.png'; // 닫힘 상태 이미지

function Dashboard() {
  const [sensorStatus, setSensorStatus] = useState('닫힘'); // 접근 센서 상태 예시값
  const containerStyle = {
    width: '100%',
    height: '400px',
  };

  const center = {
    lat: 37.7749, // 지도 중심 위도
    lng: 126.9780, // 지도 중심 경도 (예: 서울 시청)
  };

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=YOUR_KAKAO_MAPS_API_KEY&autoload=false`;
    script.async = true;

    script.onload = () => {
      kakao.maps.load(() => {
        const mapContainer = document.getElementById('kakao-map');
        const mapOption = {
          center: new kakao.maps.LatLng(center.lat, center.lng),
          level: 3,
        };

        const map = new kakao.maps.Map(mapContainer, mapOption);

        const markerPosition = new kakao.maps.LatLng(center.lat, center.lng);
        const marker = new kakao.maps.Marker({
          position: markerPosition,
        });
        marker.setMap(map);
      });
    };

    document.body.appendChild(script);
  }, []);

  return (
    <div className="dashboard-container">
      {/* 헤더 */}
      <header className="header">
        <div className="logo">로고</div>
        <nav className="nav">
          <a href="/" className="link">홈</a>
          <a href="/dashboard" className="link">대시보드</a>
          <a href="/settings" className="link">설정</a>
        </nav>
      </header>

      <div className="map-section" style={{ marginTop: '50px' }}>
        <h2>배송 위치</h2>
        <div id="kakao-map" style={containerStyle}></div>
      </div>

      <div className="data-section" style={{ display: 'flex', marginTop: '30px' }}>
        {/* 데이터 시각화 */}
        <div style={{ flex: 1 }}>
          <h2>데이터 시각화</h2>
          <DataGraph />
        </div>

        {/* 센서 상태 */}
        <div className="sensor-status">
          <h2>상자 상태</h2>
          <table>
            <thead>
              <tr>
                <th>센서</th>
                <th>상태</th>
                <th>설명</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>접근 센서</td>
                <td>{sensorStatus}</td>
                <td>{sensorStatus === '열림' ? '상자가 열려 있습니다.' : '상자가 닫혀 있습니다.'}</td>
              </tr>
            </tbody>
          </table>
          <img
            src={sensorStatus === '열림' ? openBoxImg : closedBoxImg}
            alt="Box Status"
            style={{ marginTop: '20px' }}
          />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
