import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import './App.css';

const Dashboard = () => {
    const [notifications, setNotifications] = useState([]);
    const [sensorData, setSensorData] = useState({ shock: '', temperature: '', opend: '' });
    const socketRef = useRef(null); // 소켓 객체를 관리

    // 소켓 연결 및 알림 처리
    useEffect(() => {
        // 소켓 연결
        socketRef.current = io('http://127.0.0.1:5000', {
            transports: ['websocket'], // WebSocket 사용 강제
            reconnectionAttempts: 5, // 재연결 시도 횟수
        });

        // 소켓 연결 성공
        socketRef.current.on('connect', () => {
            console.log('Connected to server');
        });

        // 알림 이벤트 수신
        socketRef.current.on('notification', (data) => {
            console.log('Received notification:', data);
            setNotifications((prev) => [...prev, data.message]);
            alert(`New Notification: ${data.message}`); // 알림창
        });

        // 연결 오류 처리
        socketRef.current.on('connect_error', (err) => {
            console.error('Connection error:', err);
        });

        // 컴포넌트 언마운트 시 소켓 정리
        return () => {
            if (socketRef.current) {
                socketRef.current.disconnect();
                console.log('Disconnected from server');
            }
        };
    }, []); // 빈 배열로 설정하여 한 번만 실행

    // Sensor Data Fetch
    const fetchSensorData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/aaaa', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
            });
            const jsonData = await response.json();
            setSensorData(jsonData);
        } catch (error) {
            console.error('Error fetching sensor data:', error);
        }
    };

    return (
        <div className="dashboard">
            {/* 왼쪽 패널: 실시간 위치 정보 */}
            <div className="left-panel">
                <h2>Real-Time Location</h2>
                <div className="location-box">
                    <p>Latitude: --</p>
                    <p>Longitude: --</p>
                    <p>Status: Updating...</p>
                </div>
            </div>

            {/* 오른쪽 패널: 센서 데이터 및 알림 */}
            <div className="right-panel">
                <h2>Sensor Data</h2>
                <div className="sensor-box">
                    <p><strong>Shock:</strong> {sensorData.shock || 'Loading...'}</p>
                    <p><strong>Temperature:</strong> {sensorData.temperature || 'Loading...'}</p>
                    <p><strong>Opend:</strong> {sensorData.opend || 'Loading...'}</p>
                </div>
                <button onClick={fetchSensorData}>Fetch Data</button>

                {/* 알림 리스트 */}
                <div className="notifications">
                    <h3>Notifications</h3>
                    <ul>
                        {notifications.map((notif, index) => (
                            <li key={index}>{notif}</li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
